import '../index.scss'
import './blog.scss'
import Header from '../../partials/head';
import {GlobalNavbar} from '../../partials/components';
import React from "react";
import fs from "fs";
import matter from "gray-matter";

export default function Blog({posts}) {
    return (
    <div>
        <Header page_name="Blog"/>
        {posts.map(({ frontmatter: { title, description, date } }) => (
        <article key={title}>
          <header>
            <h3>{title}</h3>
            <span>{date}</span>
          </header>
          <section>
            <p>{description}</p>
          </section>
        </article>
      ))}
        <GlobalNavbar/>
        <script></script>
    </div>
    );
}
export async function getStaticProps() {
    const files = fs.readdirSync(`${process.cwd()}../../posts`);

  const posts = files.map((filename) => {
    const markdownWithMetadata = fs
      .readFileSync(`../../posts/${filename}`)
      .toString();

    const { data } = matter(markdownWithMetadata);

    // Convert post date to format: Month day, Year
    const options = { year: "numeric", month: "long", day: "numeric" };
    const formattedDate = data.date.toLocaleDateString("en-US", options);

    const frontmatter = {
      ...data,
      date: formattedDate,
    };

    return {
      slug: filename.replace(".md", ""),
      frontmatter,
    };
  });

  return {
    props: {
      posts,
    },
  };
}
