import Header from '../../partials/head';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import '../index.scss'
import Graph from "react-graph-vis";
import {
    ColoredButton,
    GlobalNavbar,
    ProjectList,
    StubsList,
    GlobalFooter,
    Divider,
    Stub
} from '../../partials/components';
import React from "react";


class Majors extends React.Component {
    constructor(props) {
        super(props);
        this.state = {loading: true};
        const majors = {

            CS: {
                CS1: {
                    id: 1,
                    title: "CS 1",
                    dependencies: []
                },
                CS2: {
                    id: 2,
                    title: "CS 2",
                    dependencies: [1]
                },
                CS3: {
                    id: 3,
                    title: "CS 3",
                    dependencies: [2]
                },
                CS4: {
                    id: 4,
                    title: "CS 4",
                    dependencies: [1]
                }
            }
        }
        this.graph = {
            nodes: [],
            edges: [
              { from: 1, to: 2 },
              { from: 1, to: 3 },
              { from: 2, to: 4 },
              { from: 2, to: 5 }
            ]
          };
        Object.entries(majors.CS).forEach(([key, value]) => {
            this.graph.nodes.push({id: value.id, label: value.title, title: ""});
            value.dependencies.forEach((v) => {
                this.graph.edges.push({from: value.id, v})
            })
        });

        this.options = {
            layout: {
              hierarchical: true
            },
            edges: {
              color: "#000000"
            },
            height: "500px"
          };

         this.events = {
            select: function(event) {
              var { nodes, edges } = event;
            }
          };
    }

    componentDidMount() {
        this.setState({loading: false});
    }

    mainContent() {
        return (
            <div>
                <Header page_name="SURF Archive"/>
                <GlobalNavbar/>
                <div id='rundown-wrapper'>
                    <Container id='rundown-jim'>
                        <p className='nm'>SURF Archive Tool</p>
                        <div className='ds'>Search through past projects at Caltech's Summer Undergraduate Research Program</div>
                    </Container>
                </div>
                <div className='main-content'>
                    <Container>
                        <Row>
                        <Graph
                          graph={this.graph}
                          options={this.options}
                          events={this.events}
                          getNetwork={network => {
                            //  if you want access to vis.js network api you can set the state in a parent component using this property
                          }}
                        />
                        </Row>
                    </Container>
                </div>
                <GlobalFooter/>
            </div>
        )
    }

    render() {

        return (
            this.mainContent()
        )
    }
}

export default Majors;
