import Header from '../../partials/head';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import '../index.scss'
import Graph from "react-graph-vis";
import {
    ColoredButton,
    GlobalNavbar,
    ProjectList,
    StubsList,
    GlobalFooter,
    Divider,
    Stub
} from '../../partials/components';
import React from "react";


class Majors extends React.Component {
    constructor(props) {
        super(props);
        this.state = {loading: true};
        const majors = {

            CS: {
                Major: {
                    id: 0,
                    title: "CS Major",
                    dependencies: [1, 2, 3, 4, 11, 21, 24, 38, 122, 124, 139, 142, 143, 150, 151]
                },
                CS1: {
                    id: 1,
                    title: "CS 1",
                    dependencies: []
                },
                CS2: {
                    id: 2,
                    title: "CS 2",
                    dependencies: [1]
                },
                CS3: {
                    id: 3,
                    title: "CS 3",
                    dependencies: [2]
                },
                CS4: {
                    id: 4,
                    title: "CS 4",
                    dependencies: [1]
                },
                Ma6a: {
                    id: 5,
                    title: "Ma 6a",
                    dependencies: []
                },
                CS11: {
                    id: 11,
                    title: "CS 11",
                    dependencies: [1]
                },
                CS21: {
                    id: 21,
                    title: "CS 21",
                    dependencies: [2]
                },
                CS22: {
                    id: 22,
                    title: "CS 22",
                    dependencies: [2]
                },
                CS38: {
                    id: 38,
                    title: "CS 38",
                    dependencies: [2, 5, 21]
                },
                CS24: {
                    id: 24,
                    title: "CS 24",
                    dependencies: [2, 3]
                },
                CS121: {
                    id: 121,
                    title: "CS 121",
                    dependencies: [1]
                },
                CS122: {
                    id: 122,
                    title: "CS 122",
                    dependencies: [121]
                },
                CS123: {
                    id: 123,
                    title: "CS 123",
                    dependencies: [121, 122]
                },
                CS124: {
                    id: 124,
                    title: "CS 124",
                    dependencies: [24]
                },
                CS171: {
                    id: 171,
                    title: "CS 171",
                    dependencies: [2]
                },
                CS174: {
                    id: 174,
                    title: "CS 174",
                    dependencies: [171]
                },
            }
        }
        this.graph = {
            nodes: [],
            edges: []
          };
        Object.entries(majors.CS).forEach(([key, value]) => {
            this.graph.nodes.push({id: value.id, label: value.title, title: "", color: "white"});
            value.dependencies.forEach(v => {
                this.graph.edges.push({from: value.id, to: v, color: "#ff8c69"})
            })
        });

        this.options = {
            layout: {
              hierarchical: true
            },
            edges: {
              color: "#000000"
            },
            height: "700px"
          };

         this.events = {
            select: function(event) {
              var { nodes, edges } = event;
            }
          };
    }

    componentDidMount() {
        this.setState({loading: false});
    }

    mainContent() {
        return (
            <div>
                <Header page_name="SURF Archive"/>
                <GlobalNavbar/>
                <div id='rundown-wrapper'>
                    <Container id='rundown-jim'>
                        <p className='nm'>SURF Archive Tool</p>
                        <div className='ds'>Search through past projects at Caltech's Summer Undergraduate Research Program</div>
                    </Container>
                </div>
                <div className='main-content'>
                    <Container>
                        <Row>
                        <Graph
                          graph={this.graph}
                          options={this.options}
                          events={this.events}
                          getNetwork={network => {
                            //  if you want access to vis.js network api you can set the state in a parent component using this property
                          }}
                        />
                        </Row>
                    </Container>
                </div>
                <GlobalFooter/>
            </div>
        )
    }

    render() {

        return (
            this.mainContent()
        )
    }
}

export default Majors;
